mapper.select:
SELECT re.RE_CODE ,pd.MODEL_CODE ,pd.MODEL_NAME ,pd.PD_CODE ,pd.PD_NAME ,pd.PD_CORP as pdcorp ,pd.PD_PRICE ,re.RE_DATE ,re.RE_AMT ,re.MODEL_CODE ,re.WH_CODE ,ni.loginID
from DIRECTION d join RETURN_INFO re on d.DIR_CODE = re.DIR_CODE join PD_INFO pd on re.MODEL_CODE = pd.MODEL_CODE join JORDER_INFO jo on jo.JORD_CODE = re.JORD_CODE join tb_userinfo us on us.loginID = jo.loginID join BORDER_INFO bi on bi.BORD_CODE = re.BORD_CODE join NAP_INFO ni on bi.NAP_CODE = ni.NAP_CODE
where re.RE_CODE = #{re_code}
GROUP by d.DIR_CODE limit #{pageIndex}, #{pageSize}

SELECT count(*)
from DIRECTION d join RETURN_INFO re on d.DIR_CODE = re.DIR_CODE join PD_INFO pd on re.MODEL_CODE = pd.MODEL_CODE
where 1=1 and re.RE_CODE = #{re_code}

mapper.update:
      	UPDATE	RETURN_INFO re

		set		re.RE_DATE = DATE_FORMAT(now(), '%Y%m%d')
		where re.RE_CODE = #{re_code}

      	update	WHOUSE_STOCK ws 
		set		ws.WH_STOCK = ws.WH_STOCK  + #{re_amt}
		where 	ws.WH_CODE = #{wh_code}
				and ws.MODEL_CODE = #{model_code}

mapper.insert:
      insert into WHOUSE_HOUSING(
								WH_NO
								,WH_CODE
								,MODEL_CODE
								,loginID
								,WH_TYPE
								,WH_AMT
								,WH_DATE
								)
						values(
								(select max(WH_NO)+1 from WHOUSE_HOUSING w)
								,#{wh_code}
								,#{model_code}
								,#{loginID}
								,0
								,#{re_amt}
								,DATE_FORMAT(now(), '%Y%m%d')
								)
